    function getProductArrayBasedOnQuerystring() {
        $arrayToReturn;
        
        // If the user passed in a variable 
        if(isset($_GET['category'])) {
            $qs_category = $_GET['category'];
        } else {
            // REDIRECT THE USER
            $qs_category = "dinnerplates";
        }
        
        switch ($qs_category) {
          case "dinnerplates":
            $arrayToReturn = "dinnerplates";
            break;
          case "mugs":
            $arrayToReturn = "mugs";
            break;
          default:
            // REDIRECT THE USER
            $arrayToReturn = "dinnerplates";
            // REDIRECT THE USER
        }
        
        return $GLOBALS[$arrayToReturn];
    }

